mapper.select:
	    SELECT	COUNT(*) as basketNo
        FROM	BASKET
        WHERE	FAMILY_CUST_NO = #{familyCustNo}
        AND		PRODUCT_NO = #{productNo}
        AND		USE_YN = 'Y'

mapper.update:
UPDATE	BASKET
SET		USE_YN = 'W'
,		CHG_DT = NOW()
,		CHG_CUST_NO = #{chgCustNo}
WHERE	BASKET_NO = #{basketNo}
AND		FAMILY_CUST_NO = #{familyCustNo}
AND		USE_YN = 'Y'
