mapper.select:
SELECT	COUNT(*)
FROM	CUSTOM_CREDIT
WHERE	FAMILY_CUST_NO = #{familyCustNo}
AND		USE_YN = 'Y'
HAVING	SUM(CREDIT_VALUE) >= #{creditValue}

mapper.update:
UPDATE	FAMILY_CUSTOM
SET		FAMILY_CUST_NICKNAME = #{familyCustNick}
,		CHG_CUST_NO = #{custNo}
,		CHG_DT = NOW()
WHERE	FAMILY_CUST_NO = #{familyCustNo}
AND		USE_YN = 'Y'

mapper.select:
SELECT	COUNT(*)
FROM	FAMILY_CUSTOM 
WHERE	FAMILY_CUST_NICKNAME= #{familyCustNickname}
AND		USE_YN='Y'
LIMIT 1;
