mapper.select:
SELECT	FAMILY_CUST_NICKNAME
FROM	FAMILY_CUSTOM 
WHERE	FAMILY_CUST_NICKNAME= #{familyCustNickname}
AND		USE_YN='Y'
LIMIT 1;

	SELECT	DISTINCT	DONG AS DONG
    FROM	ZIPCODE
    WHERE	SIDO = #{sido}
    AND		GUGUN = #{gugun}
    UNION DISTINCT
    SELECT	DISTINCT	DONG_KOR AS DONG
    FROM	NEW_ZIPCODE  
    WHERE	SI_DO_KOR = #{sido}
    AND		SI_GUN_GU_KOR = #{gugun}
    ORDER BY DONG ASC

   	select	DISTINCT
   			TRIM(CONCAT(RI,' ', BUNJI)) as ri
		,		LEFT(ZIPCODE,3) as zipCode1
		,		RIGHT(ZIPCODE,3) as zipCode2
       from	ZIPCODE 
       WHERE	SIDO = #{sido}
       AND		GUGUN = #{gugun}
       AND		DONG = #{dong}
       AND		RI != ''
       AND		BUNJI != ''
       UNION DISTINCT
       select	DISTINCT ROAD_NAME_KOR as ri
       ,		null as zipCode1
		,		null as zipCode2
       from	NEW_ZIPCODE 
       WHERE	SI_DO_KOR = #{sido}
       AND		SI_GUN_GU_KOR = #{gugun}
       AND		DONG_KOR = #{dong}
       ORDER BY ri ASC
